(window.webpackJsonp=window.webpackJsonp||[]).push([[24],{639:function(n,t,a){"use strict";a.r(t);var o=a(5),e=Object(o.a)({},(function(){var n=this,t=n._self._c;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("p",[n._v("从毕业到现在，我的笔记软件经过了 印象笔记->马克飞象->为知笔记-> MWeb->Notion->Bear->Obsidian 一系列的变迁，在这期间也尝试过一些其他的软件（包括但不限于飞书、wolai、语雀、iOS 备忘录、石墨笔记、幕布等），可以说在记笔记这件事情上践行了 “路漫漫其修远兮，吾将上下而求索”。​")]),n._v(" "),t("blockquote",[t("p",[n._v("这里只是一个静态存档，完整文章请到 "),t("a",{attrs:{href:"https://x0ibii956s.feishu.cn/docs/doccnvhOKenYnKlu9h5Qpo5SwXg",target:"_blank",rel:"noopener noreferrer"}},[n._v("飞书"),t("OutboundLink")],1),n._v(" 查看")])]),n._v(" "),t("p",[n._v("在最开始使用各种笔记软件的时候，我的注意力都放在了 “笔记软件功能全不全” 这件事情上而忽视了记笔记最重要的原则：管理自己的知识。从去年（2021）年底开始，我开始重新思考到底什么样的笔记才能帮助我管理自己的知识，在参考了 “卡片笔记法” 的一些文章后，我慢慢总结了自己记笔记的一些原则，并从今年（2022）上半年开始，按照这些原则将所有的笔记从 Bear 迁移到了 Obsidian，这些原则总结下来有三点：​")]),n._v(" "),t("ul",[t("li",[n._v("链接：新笔记要尽力和已有的笔记进行链接​")]),n._v(" "),t("li",[n._v("组网：笔记之间只要存在关联即可添加链接，不纠结某个笔记属于哪个分类​")]),n._v(" "),t("li",[n._v("卡片化：把每个笔记当做是一张卡片，内容尽量精简​\n在这个过程中感受到了一些用文件夹、标签组织的树状笔记所不具有的一些优点：​")]),n._v(" "),t("li",[n._v("提升记笔记欲望：看着链接生成的网状图，会非常有记笔记的动力​")]),n._v(" "),t("li",[n._v("提升笔记结构合理度：可以图形化审视自己的知识体系​")]),n._v(" "),t("li",[n._v("降低回顾成本：内容短，回顾成本低​\n提升记笔记欲望​\n当我将我的笔记整理完后，我会得到这样的一张图：​\n​\n"),t("img",{attrs:{src:"https://internal-api-drive-stream.feishu.cn/space/api/box/stream/download/all/boxcns8aZCI6YQ5ziHL0FqgwgLL/?mount_node_token=doccnW8LkyCwjCYBWVThAyaoace&mount_point=doc_image",alt:""}}),n._v("\n​\n​\n从图中的 ROOT 节点出发，所有的笔记相互之间都是有关联的，我可以非常方便的从一个笔记跳转到另外一个笔记中。同时当我学习🥱的时候，我可以点击图谱按钮，看一看我记录的所有的笔记，用这张大网产生的成就感来催促自己继续学习：​\n​\n​​\n提升笔记结构合理度​\n网状笔记的最大好处，就是所有的笔记之间都有关联，而大量关联其他笔记的笔记（在我的笔记系统中这种类型的笔记被称作是 Map）一般都代表着这些笔记之间的共性，通过这种方式，我可以以非常轻松的方式组织所有的笔记，进而通过这个网状笔记就能够明确 “我知道什么”，甚至是 “我还不知道什么”。​\n例如，在我的笔记系统中，所有的笔记往上追溯，都会追溯到这个 ROOT 节点：​\n​\n"),t("img",{attrs:{src:"https://internal-api-drive-stream.feishu.cn/space/api/box/stream/download/all/boxcnsT4K0oKEgJm3tmmuue0PNe/?mount_node_token=doccnW8LkyCwjCYBWVThAyaoace&mount_point=doc_image",alt:""}}),n._v("\n​\n​\n而 ROOT 节点又会通过链接，链接到其他的笔记：​\n​\n"),t("img",{attrs:{src:"https://internal-api-drive-stream.feishu.cn/space/api/box/stream/download/all/boxcng09z5N2b9rgKmpbzzspyBb/?mount_node_token=doccnW8LkyCwjCYBWVThAyaoace&mount_point=doc_image",alt:""}}),n._v("\n​\n​\n而其他的笔记，会越来越具象化，例如，ROOT -> 计算机 -> 网络 -> TCP：​\n​\n|\n层级​\n|\n笔记内容​\n|\n|\n计算机​\n|\n​\n"),t("img",{attrs:{src:"https://internal-api-drive-stream.feishu.cn/space/api/box/stream/download/all/boxcnlHBav8k7tfNb7HD054EYLc/?mount_node_token=doccnW8LkyCwjCYBWVThAyaoace&mount_point=doc_image",alt:""}}),n._v("\n​\n​\n​\n|\n|\n网络​\n|\n​\n"),t("img",{attrs:{src:"https://internal-api-drive-stream.feishu.cn/space/api/box/stream/download/all/boxcn4jIHNgKX0fz8rfVHgVBgEe/?mount_node_token=doccnW8LkyCwjCYBWVThAyaoace&mount_point=doc_image",alt:""}}),n._v("\n​\n​\n​\n|\n|\nTCP​\n|\n​\n"),t("img",{attrs:{src:"https://internal-api-drive-stream.feishu.cn/space/api/box/stream/download/all/boxcnSaIxYPGxWUuuVHMFAbijQh/?mount_node_token=doccnW8LkyCwjCYBWVThAyaoace&mount_point=doc_image",alt:""}}),n._v("\n​\n​\n​\n|\n​")])])])}),[],!1,null,null,null);t.default=e.exports}}]);